[Export]
[TagType(typeof(T))]
[ContentType("any")]
class TaggerProvider : ITaggerProvider
{
    [Import]
    ITextSearchTaggerFactoryService searchTaggerFactory;

    #region ITaggerProvider Members

    public ITagger<T> CreateTagger<T>(Microsoft.VisualStudio.Text.ITextBuffer buffer) where T : ITag
    {
        ITextSearchTagger<T> tagger = searchTaggerFactory.CreateTextSearchTagger<T>(buffer);

        tagger.TagTerm(...);

        return tagger as ITagger<T>;
    }

    #endregion
}

